document.addEventListener("DOMContentLoaded", () => {
    const manuscript = new Manuscript();
    manuscript.setup({
        font: FONT_MATRIX["Parisienne"].name,
        fontSize: "10px",
        startFill: "transparent",
        endFill: "black",
        startStroke: "transparent",
        endStroke: "black",
        baseAnimationDuration: 2500,
        textFillExtraAnimationDuration: 3500,
    });
    const textId = manuscript.write("My Name Is Optimus Prime!", { letterConfiguration: { delay: 150 } });
    manuscript.erase(textId)
})
